mapper.update:
UPDATE review
SET viewcount = viewcount+1
WHERE num=#{num}

mapper.select:
SELECT result1.* FROM (SELECT num, writer, title, content, viewcount, TO_CHAR(regdate,'YY-MM-DD HH24:MI') AS regdate, LAG(num,1,0) OVER (ORDER BY num DESC) AS prevNum, LEAD(num,1,0) OVER (ORDER BY num DESC) AS nextNum FROM review
WHERE title LIKE '%'||#{title}||'%' OR content LIKE '%'||#{content}||'%'
WHERE title LIKE '%'||#{title}||'%'
WHERE writer LIKE '%'||#{writer}||'%'
) result1 WHERE num=#{num}

mapper.update:
UPDATE review
SET title=#{title}, content=#{content}
WHERE num=#{num}
