mapper.update:
UPDATE res_state
SET state = '매진'
WHERE bname=#{bname} AND resdate=#{resdate} AND time=#{time} AND thema=#{thema}

UPDATE res_state
SET state = '예약진행중'
WHERE bname=#{bname} AND resdate=#{resdate} AND time=#{time} AND thema=#{thema}

UPDATE res_state
SET blocktime= TO_CHAR(SYSDATE+5/(24*60),'HH24:MI:SS')
WHERE bname=#{bname} AND  resdate=#{resdate} AND time=#{time} AND thema=#{thema}
